export enum CourseStatus {
  NEW = "NEW",
  ONGOING = "ONGOING",
  COMPLETED = "COMPLETED",
}

export interface Category {
  id: string;
  name: string;
  icon: string;
}

export interface Lesson {
  id: string;
  title: string;
}

export interface Course {
  id: string;
  status: CourseStatus;
  title: string;
  category: Category;
  thumbnail: string;
  lessons: Lesson[];
  description: string;
  provider?: string;
  certificateType: string;
}

